import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InformacoesGerais = () => {
  const [data, setData] = useState(null);
  const [quantOnline, setClienteOnline] = useState(0);
  const [clientesPorPlano, setClientesPorPlano] = useState({});
  const [totalReceita, setTotalReceita] = useState(0);
  const [consumoTotalDownload, setConsumoTotalDownload] = useState(0);
  const [consumoTotalUpload, setConsumoTotalUpload] = useState(0);
  const [mediaTempoConectado, setMediaTempoConectado] = useState(0);

  const infoClientes = async () => {
    try {
      const response = await axios.get("http://localhost:3333/findClienteByName?name=");
      setData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    infoClientes();
  }, []);

  useEffect(() => {
    if (data) {
      const quantOnline = data.reduce((count, cliente) => {
        return cliente.statusCliente ? count + 1 : count;
      }, 0);
      setClienteOnline(quantOnline);

      const quantClientesPlanos = data.reduce((acc, cliente) => {
        const plano = cliente.planoContrato;
        if (acc[plano]) {
          acc[plano]++;
        } else {
          acc[plano] = 1;
        }
        return acc;
      }, {});
      setClientesPorPlano(quantClientesPlanos);

      const totalReceita = data.reduce((total, cliente) => total + cliente.valorPlano, 0);
      setTotalReceita(totalReceita);

      const consumoTotalDownload = data.reduce((total, cliente) => total + Number(cliente.consumoDownload), 0);
      setConsumoTotalDownload(consumoTotalDownload / (1024 ** 4)); // Convert bytes to TB

      const consumoTotalUpload = data.reduce((total, cliente) => total + Number(cliente.consumoUpload), 0);
      setConsumoTotalUpload(consumoTotalUpload / (1024 ** 4)); // Convert bytes to TB

      const totalTempoConectado = data.reduce((total, cliente) => total + Number(cliente.tempoConectado), 0);
      const mediaTempoConectado = data.length ? totalTempoConectado / data.length : 0;
      setMediaTempoConectado(mediaTempoConectado);
    }
  }, [data]);

  return (
    <div>
      <p>Clientes Online: {quantOnline}</p>
      <p>Total Receita: R$ {totalReceita.toFixed(2)}</p>
      <p>Consumo Total Download: {consumoTotalDownload.toFixed(2)} TB</p>
      <p>Consumo Total Upload: {consumoTotalUpload.toFixed(2)} TB</p>
      <p>Média Tempo Conectado: {mediaTempoConectado.toFixed(2)} segundos</p>
      {data && (
        <div>
          <h2>Clientes:</h2>
          {data.map((cliente) => (
            <div key={cliente.id}>
              <p>{JSON.stringify(cliente, null, 2)}</p>
            </div>
          ))}
        </div>
      )}
      {clientesPorPlano && (
        <div>
          <h2>Clientes por Plano:</h2>
          {Object.keys(clientesPorPlano).map((plano) => (
            <p key={plano}>
              {plano}: {clientesPorPlano[plano]}
            </p>
          ))}
        </div>
      )}
    </div>
  );
};

export default InformacoesGerais;
