import React, { useState } from 'react';
import axios from 'axios';

const ClientePesquisa = () => {
  const [nomeCliente, setNomeCliente] = useState('');
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showDetails, setShowDetails] = useState(false);

  const pesquisarCliente = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:3333/findClienteByName?name=${nomeCliente}`);
      setData(response.data);
      setError(null);
      setShowDetails(true);
    } catch (error) {
      setError(error);
      setData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (event) => {
    setNomeCliente(event.target.value);
  };

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  return (
    <div>
      <h1>Pesquisar Cliente</h1>
      <label>
        <span>Informe o nome do cliente: </span>
        <input value={nomeCliente} onChange={handleInputChange} name="myInput" />
      </label>
      <button onClick={pesquisarCliente}>
        Pesquisar
      </button>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error.message}</p>}
      {showDetails && data && (
        <div>
          <h2>Dados dos Clientes:</h2>
          {data.map((cliente) => (
            <div key={cliente.id}>
              <h3 className='nomeCliente'>{cliente.nomeCliente}</h3>
              <button onClick={toggleDetails}>
                {showDetails ? 'Ocultar Detalhes' : 'Mostrar Detalhes'}
              </button>
              {showDetails && (
                <div>
                  <p>{cliente.statusCliente}</p>
                  <p>{cliente.ipConcentrador}</p>
                  <p>{cliente.nomeConcentrador}</p>
                  <p>{cliente.latitudeCliente}</p>
                  <p>{cliente.longitudeCliente}</p>
                  <p>{cliente.conexaoInicial}</p>
                  <p>{cliente.conexaoFinal}</p>
                  <p>{cliente.tempoConectado}</p>
                  <p>{cliente.consumoDownload}</p>
                  <p>{cliente.consumoUpload}</p>
                  <p>{cliente.motivoDesconexao}</p>
                  <p>{cliente.popCliente}</p>
                  <p>{cliente.enderecoCliente}</p>
                  <p>{cliente.bairroCliente}</p>
                  <p>{cliente.cidadeCliente}</p>
                  <p>{cliente.planoContrato}</p>
                  <p>{cliente.statusInternet}</p>
                  <p>{cliente.valorPlano}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ClientePesquisa;
