import React, { useState } from 'react';
import axios from 'axios';

const PesquisarCliente = () => {
  const [nomeCliente, setNomeCliente] = useState('');
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const pesquisarCliente = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:3333/findClienteByName?name=${nomeCliente}`);
      setData(response.data);
      setError(null);
    } catch (error) {
      setError(error);
      setData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (event) => {
    setNomeCliente(event.target.value);
  };

  return (
    <div>
      <label>
        <span>Informe o nome do cliente: </span>
        <input value={nomeCliente} onChange={handleInputChange} name="myInput" />
      </label>
      <button onClick={pesquisarCliente}>
        Pesquisar
      </button>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error.message}</p>}
      {data && (
        <div>
          <h2>Dados do Cliente:</h2>
          <pre>{JSON.stringify(data, null, 2)}</pre>
          <h2>{data}.nomeCliente</h2>
        </div>
      )}
    </div>
  );
};

export default PesquisarCliente;
